import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation } from "react-router";
import { GetCustomer } from "../actions/customerActions";
import _ from "lodash";
import "../App.css";

const CustomerPage = (props) => {
  const location = useLocation();
  const customerId = location.state.params;
  const dispatch = useDispatch();
  const customerItem = useSelector((state) => state.CustomerItem);

  useEffect(() => {
    FetchData();
  }, []);

  const FetchData = () => {
    dispatch(GetCustomer(customerId));
  };

  const ShowData = () => {
    if (!_.isEmpty(customerItem.data)) {
      return (
        <>
          <p>{customerItem.data.id}</p>
          <p>{customerItem.data.firstName}</p>
          <p>{customerItem.data.lastName}</p>
          <p>{customerItem.data.email}</p>
          <p>{customerItem.data.customerSince}</p>
        </>
      );
    }

    if (customerItem.loading) {
      return <p>loading...</p>;
    }
    if (customerItem.errMsg !== "") {
      return <p>{customerItem.errMsg}</p>;
    }
    return <p>unable get data</p>;
  };

  return <p>{ShowData()}</p>;
};

export default CustomerPage;
