import React from "react";
import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";
import { GetCustomerList } from "../actions/customerActions";
import CustomerItem from "../components/CustomerItem";
import styled from "styled-components";

const CustomerTable = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 0px auto 0px auto;
`;

const CustomerHeader = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 40px auto 0px auto;
`;

const CustomerList = () => {
  const dispatch = useDispatch();
  const customerList = useSelector((state) => state.CustomerList);

  React.useEffect(() => {
    FetchData();
  }, []);

  const FetchData = () => {
    dispatch(GetCustomerList);
  };
  const ShowData = () => {
    if (!_.isEmpty(customerList.data)) {
      console.log(customerList.data);
      return (
        <>
          <CustomerHeader>
            <CustomerItem
              key={"header"}
              id={"ID"}
              firstName={"firstName"}
              lastName={"lastName"}
              email={"email"}
              customerSince={"customerSince"}
            ></CustomerItem>
          </CustomerHeader>
          <CustomerTable></CustomerTable>
        </>
      );
    }

    if (customerList.loading) {
      return <p>loading...</p>;
    }
    if (customerList.errMsg !== "") {
      return <p>{customerList.errMsg}</p>;
    }

    return <p>unable get data</p>;
  };

  return <p>{ShowData()}</p>;
};

export default CustomerList;
