import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import _ from "lodash";
import { GetCustomerList } from "../actions/customerActions";
import CustomerItem from "../components/CustomerItem";
import styled from "styled-components";
import { useHistory } from "react-router-dom";

const CustomerTable = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 0px auto 0px auto;
`;

const CustomerHeader = styled.div`
  display: flex;
  flex-direction: column;
  width: 80%;
  margin: 40px auto 0px auto;
`;

const CustomerList = () => {
  const history = useHistory();
  const dispatch = useDispatch();
  const customerList = useSelector((state) => state.CustomerList);

  const [addone, setaddone] = useState(false);
  const [addmultiple, setaddmultiple] = useState(false);
  const [deletecustomer, setdeletecustomer] = useState(false);

  const handleCustomerClick = (userid) => {
    history.push("/customer/" + userid);
  };
  useEffect(() => {
    FetchData();
  }, []);

  const FetchData = () => {
    dispatch(GetCustomerList());
  };

  const ShowData = () => {
    if (!_.isEmpty(customerList.data)) {
      return (
        <>
          {!addmultiple && !addone && !deletecustomer && (
            <CustomerAddButtons>
              <button onClick={() => setaddone(true)}>Add Customer</button>
              <button onClick={() => setaddmultiple(true)}>
                Add Customers
              </button>
              <button onClick={deleteAllCustomers}>Delete all</button>
            </CustomerAddButtons>
          )}

          <CustomerHeader>
            <CustomerItem
              key={"header"}
              _id={"ID"}
              firstName={"firstName"}
              lastName={"lastName"}
              email={"email"}
              customerSince={"customerSince"}
            ></CustomerItem>
          </CustomerHeader>
          <CustomerTable>
            {customerList.data.map(
              ({ _id, firstName, lastName, email, customerSince }) => (
                <CustomerItem
                  handleCustomerClick={handleCustomerClick}
                  key={_id}
                  _id={_id}
                  firstName={firstName}
                  lastName={lastName}
                  email={email}
                  customerSince={customerSince}
                ></CustomerItem>
              )
            )}
          </CustomerTable>
        </>
      );
    }

    if (customerList.loading) {
      return <p>loading...</p>;
    }
    if (customerList.errMsg !== "") {
      return <p>{customerList.errMsg}</p>;
    }
    return <p>unable get data</p>;
  };

  return <p>{ShowData()}</p>;
};

export default CustomerList;
